package com.hzr.demo.config;import com.hzr.demo.common.BaseConfig;import com.hzr.demo.common.FileSingUtils;import org.springframework.boot.SpringBootConfiguration;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;/** * @Classname BeanConfig * @Author: hzr * @Description TODO * @Version 1.0.0 * @Date 2023/12/7 15:24 * @Created by 22906 */@SpringBootConfigurationpublic class BeanConfig {    @Bean    @ConfigurationProperties(prefix = "ofd")    public BaseConfig baseConfig(){        return new BaseConfig();    }    @Bean    public FileSingUtils  fileSingUtils(){        return new FileSingUtils();    }}