package com.hzr.demo.entity;/** * @Classname ConvertEntity * @Author: hzr * @Description TODO * @Version 1.0.0 * @Date 2023/12/28 10:13 * @Created by 22906 */import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.GeneratedValue;import jakarta.persistence.Id;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import lombok.ToString;import org.hibernate.annotations.GenericGenerator;import java.time.LocalDateTime;@Data@AllArgsConstructor@NoArgsConstructor@ToString@Entitypublic class ConvertEntity {    @Id    @GeneratedValue(generator = "uuid")    @GenericGenerator(name = "uuid", strategy = "uuid2")    @Column(name = "id", unique = true)    private String id;    private String fileName;    private String filePath;    private String fileSignPath;    private String fileType;    private String fileSize;    private LocalDateTime createTime;}