package com.hzr.demo.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import lombok.ToString;import org.hibernate.annotations.GenericGenerator;import java.time.LocalDateTime;/** * @Classname file * @Author: hzr * @Description TODO * @Version 1.0.0 * @Date 2023/12/6 15:14 * @Created by 22906 */@Data@AllArgsConstructor@NoArgsConstructor@ToString@Entitypublic class FileEntity {    @Id    @GeneratedValue(generator = "uuid")    @GenericGenerator(name = "uuid", strategy = "uuid2")    @Column(name = "id", unique = true)    private String id;    private String fileName;    private String filePath;    private String fileSignPath;    private String fileType;    private String fileSize;    private LocalDateTime createTime;}