package com.hzr.demo.repository;import com.hzr.demo.entity.FileEntity;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import java.util.List;/** * @Classname FileDao * @Author: hzr * @Description TODO * @Version 1.0.0 * @Date 2023/12/6 16:21 * @Created by 22906 */@Repositorypublic interface FileRepository extends JpaRepository<FileEntity,String> {    @Query( value = "select * from file_entity where id=#1",nativeQuery = true)    FileEntity findFileById(Long id);    @Query( value = "select * from file_entity",nativeQuery = true)    List<FileEntity> findAllFile();}