package com.hzr.demo.service.impl;import com.hzr.demo.entity.FileEntity;import com.hzr.demo.mapper.FileRepository;import com.hzr.demo.service.FileService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;/** * @Classname FileServiceImpl * @Author: hzr * @Description TODO * @Version 1.0.0 * @Date 2023/12/7 10:34 * @Created by 22906 */@Servicepublic class FileServiceImpl implements FileService {    @Autowired    private FileRepository fileRepository;    @Override    public FileEntity findById(String id) {        Optional<FileEntity> byId =fileRepository.findById(id);        FileEntity fileEntity = byId.orElse(null);        return fileEntity;    }    @Override    public List<FileEntity> findAll() {        return fileRepository.findAll();    }    @Override    public FileEntity save(FileEntity file) {        return fileRepository.save(file);    }}